    #write the data in the given List in the config.ini file
    def write_List_Folder_Settings(self, folder_List):
        settings_File_Name = "./config.ini"

        try:
            with open(settings_File_Name, "w") as fp:
                for x in folder_List:
                    fp.write(x + "\n")
        finally:
            fp.close()

