    #Load all files of selected folder in the Listbox left
    def loadFiles(self):
        a=CUSANI_Support.Checkbutton1_Var.get() #check if original is selected
        b=CUSANI_Support.Checkbutton2_Var.get() #check if Treated is selected
        c=CUSANI_Support.Checkbutton3_Var.get() #check if Json is selected
        # print(type(self.File_List_Box))
        self.List_Box_Content = {}
        settings_File_Name = "./config.ini"
        prog_Location = os.path.dirname(__file__)
        settings_File_Location = os.path.join(prog_Location, settings_File_Name)

        folder_List = process_data_file.read_List_Folder_Settings() #load paths where original, Treated and Json files are

        original_Files_List = []

        treated_Files_List = []

        json_Files_List = []

        all_Files_List = []

        if (a == 1):
            prog_Location = folder_List[0] #Folder where Original photos are
            print("fetching Original Images Files in Folder:", prog_Location)

            for r, d, f in os.walk(prog_Location):
                for files in f:

                    if '.jpg' or '.jpeg' or '.png' in files: #we just select file with extension this extensions, for this case, only .jpeg files
                        print(files)
                        original_Files_List.append(files) #add File name to the ListBox
                        all_Files_List.append(files)
                        self.List_Box_Content[files] = os.path.join(r, files) #add file absolut path in a variable

            if (len(original_Files_List)) == 0:
                print("No Original Files Found")

        else:
            print("Original Files Not selected")

        if (b == 1):
            prog_Location = folder_List[1] #Folder where Treated images are
            print("fetching Treated Images Files in Folder:", prog_Location)

            for r, d, f in os.walk(prog_Location):
                for files in f:
                    if '.jpg' or '.jpeg' or '.png' in files:
                        print(files)
                        treated_Files_List.append(files)
                        all_Files_List.append(files)
                        self.List_Box_Content[files] = os.path.join(r, files)

            if (len(treated_Files_List)) == 0:
                print("No Treated Files Found")

        else:
            print("Treated Files Not selected")

        if (c == 1):
            prog_Location = folder_List[2] #Folder where json Files are
            print("fetching Json Files in Folder:", prog_Location)

            for r, d, f in os.walk(prog_Location):
                for files in f:
                    if '.json' in files:
                        print(files)
                        json_Files_List.append(files)
                        all_Files_List.append(files)
                        self.List_Box_Content[files] = os.path.join(r, files)

            if (len(json_Files_List)) == 0:
                print("No Json Files Found")
